/**
 * Comprehensive Analytics and Performance Tracking System
 * Advanced SEO analytics, content performance, and business intelligence
 */

export interface SEOAnalytics {
    keywordRankings: {
        keyword: string;
        position: number;
        previousPosition: number;
        searchVolume: number;
        difficulty: number;
        url: string;
        lastUpdated: Date;
    }[];
    organicTrafficGrowth: {
        month: string;
        organicSessions: number;
        organicUsers: number;
        organicPageViews: number;
        bounceRate: number;
        avgSessionDuration: number;
        organicConversionRate: number;
    }[];
    localSearchPerformance: {
        city: string;
        localRankings: {
            keyword: string;
            position: number;
            mapPackPosition?: number;
        }[];
        googleMyBusinessViews: number;
        googleMyBusinessClicks: number;
        directionRequests: number;
        phoneCalls: number;
    }[];
    technicalSEO: {
        coreWebVitals: {
            lcp: number; // Largest Contentful Paint
            fid: number; // First Input Delay
            cls: number; // Cumulative Layout Shift
        };
        pageSpeed: {
            desktop: number;
            mobile: number;
        };
        indexationStatus: {
            indexedPages: number;
            totalPages: number;
            crawlErrors: number;
        };
        schemaMarkup: {
            implementedSchemas: string[];
            validationErrors: number;
            richSnippetsCount: number;
        };
    };
}

export interface ContentAnalytics {
    topPerformingContent: {
        url: string;
        title: string;
        type: 'blog' | 'guide' | 'video' | 'landing-page';
        views: number;
        uniqueVisitors: number;
        avgTimeOnPage: number;
        bounceRate: number;
        socialShares: number;
        backlinks: number;
        organicTraffic: number;
        conversionRate: number;
    }[];
    contentEngagement: {
        contentType: string;
        totalViews: number;
        avgEngagementTime: number;
        socialShares: number;
        comments: number;
        conversionRate: number;
    }[];
    leadMagnetPerformance: {
        leadMagnetId: string;
        title: string;
        downloads: number;
        conversionRate: number;
        emailCaptureRate: number;
        followUpConversion: number;
        revenueGenerated: number;
    }[];
    contentGaps: {
        targetKeyword: string;
        currentPosition: number;
        estimatedTraffic: number;
        contentNeeded: string[];
        priority: 'high' | 'medium' | 'low';
    }[];
}

export interface BusinessAnalytics {
    appointmentMetrics: {
        totalAppointments: number;
        completedAppointments: number;
        cancelledAppointments: number;
        noShowAppointments: number;
        completionRate: number;
        cancellationRate: number;
        noShowRate: number;
    };
    revenueMetrics: {
        totalRevenue: number;
        revenueGrowth: number;
        avgAppointmentValue: number;
        revenuePerSession: number;
        conversionRate: number;
        customerLifetimeValue: number;
    };
    customerAnalytics: {
        newCustomers: number;
        returningCustomers: number;
        customerRetentionRate: number;
        avgSessionsPerCustomer: number;
        customerAcquisitionCost: number;
        netPromoterScore: number;
    };
    servicePerformance: {
        serviceName: string;
        bookings: number;
        revenue: number;
        avgRating: number;
        completionRate: number;
        repeatBookingRate: number;
    }[];
}

export interface CompetitorAnalytics {
    keywordGaps: {
        keyword: string;
        competitorRanking: number;
        ourRanking: number;
        opportunity: 'high' | 'medium' | 'low';
        estimatedTraffic: number;
    }[];
    contentGaps: {
        topic: string;
        competitorContentUrls: string[];
        ourContentUrls: string[];
        contentGap: number;
        priority: 'high' | 'medium' | 'low';
    }[];
    backlinkGaps: {
        domain: string;
        competitorBacklinks: number;
        ourBacklinks: number;
        domainRating: number;
        opportunity: 'high' | 'medium' | 'low';
    }[];
}

export interface ConversionFunnel {
    stage: string;
    visitors: number;
    conversionRate: number;
    dropOffRate: number;
    revenue: number;
    optimizationRecommendations: string[];
}

