/**
 * Content Marketing Automation and Distribution System
 * Automated content distribution, email marketing, and social media automation
 */

export interface AutomationWorkflow {
    id: string;
    name: string;
    type: 'email' | 'social' | 'content' | 'lead-nurture';
    trigger: {
        event: string;
        conditions: Record<string, any>;
        delay?: number;
    };
    actions: AutomationAction[];
    targetAudience: string[];
    status: 'active' | 'paused' | 'draft';
    performance: {
        totalSent: number;
        openRate: number;
        clickRate: number;
        conversionRate: number;
        lastRun: Date;
    };
}

export interface AutomationAction {
    type: 'send-email' | 'post-social' | 'create-content' | 'update-lead-score' | 'trigger-campaign';
    config: Record<string, any>;
    order: number;
}

export interface EmailCampaign {
    id: string;
    name: string;
    subject: string;
    template: string;
    audience: string[];
    schedule: {
        sendDate: Date;
        timezone: string;
        frequency: 'once' | 'daily' | 'weekly' | 'monthly';
    };
    personalization: {
        variables: string[];
        rules: string[];
    };
    a_b_test?: {
        variant: string;
        testPercentage: number;
        successMetric: string;
    };
}

export interface SocialMediaAutomation {
    platform: 'facebook' | 'instagram' | 'twitter' | 'linkedin' | 'tiktok';
    content: {
        text: string;
        hashtags: string[];
        media: string[];
        links: string[];
    };
    schedule: {
        optimalTimes: string[];
        frequency: string;
        timezone: string;
    };
    engagement: {
        autoReply: boolean;
        responseTemplates: string[];
        escalationRules: string[];
    };
}

export interface ContentDistribution {
    channel: string;
    content: {
        type: 'blog' | 'video' | 'infographic' | 'guide' | 'social-post';
        url: string;
        title: string;
        description: string;
        tags: string[];
    };
    optimization: {
        seoKeywords: string[];
        socialHashtags: string[];
        targetAudience: string[];
        distributionTime: Date;
    };
    tracking: {
        clicks: number;
        shares: number;
        conversions: number;
        reach: number;
    };
}

