export default function StrategicAutomationDashboard({ className }: StrategicAutomationDashboardProps) {
    const [dashboardData, setDashboardData] = useState<any>(null);
    const [threatData, setThreatData] = useState<ThreatDetectionStatus | null>(null);
    const [activeThreats, setActiveThreats] = useState<ActiveThreat[]>([]);
    const [opportunities, setOpportunities] = useState<OpportunityCapture[]>([]);
    const [performanceMetrics, setPerformanceMetrics] = useState<PerformanceMetrics | null>(null);
    const [automationStatus, setAutomationStatus] = useState<any>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [selectedTab, setSelectedTab] = useState('overview');

    // API functions
    const fetchDashboardOverview = useCallback(async () => {
        try {
            const response = await fetch('/api/competitive-response-engine?action=response_overview');
            const data = await response.json();
            setDashboardData(data.data);
        } catch (error) {
            console.error('Error fetching dashboard overview:', error);
        }
    }, []);

    const fetchThreatDetection = useCallback(async () => {
        try {
            const response = await fetch('/api/competitive-response-engine?action=threat_detection');
            const data = await response.json();
            // Transform to match interface
            setThreatData({
                total_rules: data.data?.length || 0,
                active_rules: data.data?.filter((rule: any) => rule.status === 'active').length || 0,
                threats_detected_today: dashboardData?.current_metrics?.threats_detected_today || 0,
                response_time_average: dashboardData?.performance_indicators?.response_time_average || '0 hours',
                automation_level: dashboardData?.automation_level || 'basic',
                success_rate: dashboardData?.performance_indicators?.success_rate || 0
            });
        } catch (error) {
            console.error('Error fetching threat detection:', error);
        }
    }, [dashboardData]);

    const fetchActiveThreats = useCallback(async () => {
        try {
            const response = await fetch('/api/competitive-response-engine?action=active_threats');
            const data = await response.json();
            setActiveThreats(data.data || []);
        } catch (error) {
            console.error('Error fetching active threats:', error);
        }
    }, []);

    const fetchOpportunities = useCallback(async () => {
        try {
            const response = await fetch('/api/competitive-response-engine?action=opportunity_capture');
            const data = await response.json();
            setOpportunities(data.data || []);
        } catch (error) {
            console.error('Error fetching opportunities:', error);
        }
    }, []);

    const fetchPerformanceMetrics = useCallback(async () => {
        try {
            const response = await fetch('/api/competitive-response-engine?action=response_performance');
            const data = await response.json();
            setPerformanceMetrics(data.data?.overall_metrics || null);
        } catch (error) {
            console.error('Error fetching performance metrics:', error);
        }
    }, []);

    const fetchAutomationStatus = useCallback(async () => {
        try {
            const response = await fetch('/api/competitive-response-engine?action=automation_status');
            const data = await response.json();
            setAutomationStatus(data.data || null);
        } catch (error) {
            console.error('Error fetching automation status:', error);
        }
    }, []);

    // Control functions
    const deployAutomatedResponse = async (threatId: number, responseStrategy?: string) => {
        try {
            const response = await fetch('/api/competitive-response-engine', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    action: 'deploy_automated_response',
                    threatId,
                    responseStrategy: responseStrategy || 'comprehensive_counter_strategy'
                })
            });
            const data = await response.json();
            if (data.success) {
                await fetchActiveThreats();
                await fetchDashboardOverview();
            }
        } catch (error) {
            console.error('Error deploying automated response:', error);
        }
    };

    const captureMarketOpportunity = async (opportunityId: number, timelineAcceleration = false) => {
        try {
            const response = await fetch('/api/competitive-response-engine', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    action: 'capture_market_opportunity',
                    opportunityId,
                    timelineAcceleration
                })
            });
            const data = await response.json();
            if (data.success) {
                await fetchOpportunities();
                await fetchDashboardOverview();
            }
        } catch (error) {
            console.error('Error capturing market opportunity:', error);
        }
    };

    const triggerPricingAdjustment = async (adjustmentType: string, targetCompetitors: string[], adjustmentMagnitude: string) => {
        try {
            const response = await fetch('/api/competitive-response-engine', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    action: 'trigger_pricing_adjustment',
                    adjustmentType,
                    targetCompetitors,
                    adjustmentMagnitude,
                    autoApproval: true
                })
            });
            const data = await response.json();
            if (data.success) {
                await fetchDashboardOverview();
            }
        } catch (error) {
            console.error('Error triggering pricing adjustment:', error);
        }
    };

    const generateResponseReport = async (reportType = 'comprehensive', timeframe = '30_days') => {
        try {
            const response = await fetch('/api/competitive-response-engine', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    action: 'generate_response_report',
                    reportType,
                    timeframe,
                    includeRecommendations: true
                })
            });
            const data = await response.json();
            if (data.success) {
                // Create download link for report
                const blob = new Blob([JSON.stringify(data.data, null, 2)], { type: 'application/json' });
                const url = URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = `competitive-response-report-${new Date().toISOString().split('T')[0]}.json`;
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                URL.revokeObjectURL(url);
            }
        } catch (error) {
            console.error('Error generating response report:', error);
        }
    };

    // Initialize data
    useEffect(() => {
        const loadData = async () => {
            setIsLoading(true);
            await Promise.all([
                fetchDashboardOverview(),
                fetchThreatDetection(),
                fetchActiveThreats(),
                fetchOpportunities(),
                fetchPerformanceMetrics(),
                fetchAutomationStatus()
            ]);
            setIsLoading(false);
        };

        loadData();

        // Set up real-time updates every 30 seconds
        const interval = setInterval(loadData, 30000);
        return () => clearInterval(interval);
    }, []);

    // Helper functions
    const getSeverityColor = (severity: string) => {
        switch (severity) {
            case 'critical': return 'destructive';
            case 'high': return 'destructive';
            case 'medium': return 'secondary';
            case 'low': return 'outline';
            default: return 'outline';
        }
    };

    const getStatusIcon = (status: string) => {
        switch (status) {
            case 'executing': return <Activity className="h-4 w-4" />;
            case 'completed': return <CheckCircle className="h-4 w-4" />;
            case 'failed': return <XCircle className="h-4 w-4" />;
            case 'pending_approval': return <Clock className="h-4 w-4" />;
            default: return <AlertTriangle className="h-4 w-4" />;
        }
    };

    const formatCurrency = (amount: number) => {
        return new Intl.NumberFormat('en-ZA', {
            style: 'currency',
            currency: 'ZAR',
            minimumFractionDigits: 0,
            maximumFractionDigits: 0
        }).format(amount);
    };

    const formatPercentage = (value: number) => {
        return `${value.toFixed(1)}%`;
    };

    if (isLoading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-center">
                    <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto"></div>
                    <p className="mt-4 text-lg font-medium">Loading Strategic Automation Dashboard...</p>
                </div>
            </div>
        );
    }

    return (
        <div className={`min-h-screen bg-gray-50 p-6 ${className}`}>
            <div className="max-w-7xl mx-auto space-y-6">
                {/* Header */}
                <div className="flex items-center justify-between">
                    <div>
                        <h1 className="text-3xl font-bold text-gray-900">Strategic Automation Dashboard</h1>
                        <p className="text-gray-600 mt-1">Executive Control Interface for Automated Competitive Response</p>
                    </div>
                    <div className="flex items-center space-x-3">
                        <Badge variant="outline" className="px-3 py-1">
                            <Activity className="h-4 w-4 mr-1" />
                            {dashboardData?.system_status || 'Operational'}
                        </Badge>
                        <Button
                            onClick={() => generateResponseReport()}
                            className="bg-blue-600 hover:bg-blue-700"
                        >
                            <Download className="h-4 w-4 mr-2" />
                            Generate Report
                        </Button>
                    </div>
                </div>

                {/* Executive Summary Cards */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                            <CardTitle className="text-sm font-medium">Response Time</CardTitle>
                            <Zap className="h-4 w-4 text-blue-600" />
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold">{performanceMetrics?.response_time_improvement || 0}%</div>
                            <p className="text-xs text-green-600">
                                {dashboardData?.performance_indicators?.response_time_average || '0 hours'} average
                            </p>
                        </CardContent>
                    </Card>

                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                            <CardTitle className="text-sm font-medium">Success Rate</CardTitle>
                            <Target className="h-4 w-4 text-green-600" />
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold">{performanceMetrics?.automation_effectiveness || 0}%</div>
                            <p className="text-xs text-green-600">
                                {dashboardData?.performance_indicators?.success_rate || 0}% effectiveness
                            </p>
                        </CardContent>
                    </Card>

                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                            <CardTitle className="text-sm font-medium">Market Protection</CardTitle>
                            <Shield className="h-4 w-4 text-purple-600" />
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold">{performanceMetrics?.market_share_protection || 0}%</div>
                            <p className="text-xs text-purple-600">
                                {performanceMetrics?.market_share_impact || '+0%'} impact
                            </p>
                        </CardContent>
                    </Card>

                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                            <CardTitle className="text-sm font-medium">ROI</CardTitle>
                            <DollarSign className="h-4 w-4 text-yellow-600" />
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold">{performanceMetrics?.roi_on_automated_responses || 0}%</div>
                            <p className="text-xs text-yellow-600">
                                Return on automated responses
                            </p>
                        </CardContent>
                    </Card>
                </div>

                {/* Main Dashboard Tabs */}
                <Tabs value={selectedTab} onValueChange={setSelectedTab} className="space-y-4">
                    <TabsList className="grid w-full grid-cols-5">
                        <TabsTrigger value="overview">Overview</TabsTrigger>
                        <TabsTrigger value="threats">Threat Management</TabsTrigger>
                        <TabsTrigger value="opportunities">Opportunities</TabsTrigger>
                        <TabsTrigger value="performance">Performance</TabsTrigger>
                        <TabsTrigger value="controls">Controls</TabsTrigger>
                    </TabsList>

                    {/* Overview Tab */}
                    <TabsContent value="overview" className="space-y-6">
                        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                            <Card>
                                <CardHeader>
                                    <CardTitle className="flex items-center">
                                        <Brain className="h-5 w-5 mr-2" />
                                        Automation Status
                                    </CardTitle>
                                    <CardDescription>Current automation levels and system health</CardDescription>
                                </CardHeader>
                                <CardContent className="space-y-4">
                                    {automationStatus?.automation_levels && Object.entries(automationStatus.automation_levels).map(([key, value]) => (
                                        <div key={key} className="flex items-center justify-between">
                                            <span className="text-sm font-medium capitalize">{key.replace(/_/g, ' ')}</span>
                                            <Badge variant="outline">{String(value)}</Badge>
                                        </div>
                                    ))}
                                </CardContent>
                            </Card>

                            <Card>
                                <CardHeader>
                                    <CardTitle className="flex items-center">
                                        <Rocket className="h-5 w-5 mr-2" />
                                        Strategic Insights
                                    </CardTitle>
                                    <CardDescription>AI-powered strategic intelligence</CardDescription>
                                </CardHeader>
                                <CardContent>
                                    <div className="space-y-3">
                                        {dashboardData?.strategic_insights?.map((insight: string, index: number) => (
                                            <Alert key={index}>
                                                <TrendingUp className="h-4 w-4" />
                                                <AlertDescription>{insight}</AlertDescription>
                                            </Alert>
                                        ))}
                                    </div>
                                </CardContent>
                            </Card>
                        </div>

                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center">
                                    <Gauge className="h-5 w-5 mr-2" />
                                    Competitive Position
                                </CardTitle>
                                <CardDescription>Real-time competitive advantage metrics</CardDescription>
                            </CardHeader>
                            <CardContent>
                                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                                    <div className="space-y-2">
                                        <div className="flex justify-between">
                                            <span className="text-sm font-medium">Market Leadership</span>
                                            <span className="text-sm text-gray-600">{dashboardData?.competitive_position?.market_leadership_score || 0}</span>
                                        </div>
                                        <Progress value={dashboardData?.competitive_position?.market_leadership_score || 0} />
                                    </div>
                                    <div className="space-y-2">
                                        <div className="flex justify-between">
                                            <span className="text-sm font-medium">Response Advantage</span>
                                            <span className="text-sm text-gray-600">{dashboardData?.competitive_position?.response_advantage_index || 0}</span>
                                        </div>
                                        <Progress value={dashboardData?.competitive_position?.response_advantage_index || 0} />
                                    </div>
                                    <div className="space-y-2">
                                        <div className="flex justify-between">
                                            <span className="text-sm font-medium">Automation Effectiveness</span>
                                            <span className="text-sm text-gray-600">{dashboardData?.competitive_position?.automation_effectiveness || 0}</span>
                                        </div>
                                        <Progress value={dashboardData?.competitive_position?.automation_effectiveness || 0} />
                                    </div>
                                    <div className="space-y-2">
                                        <div className="flex justify-between">
                                            <span className="text-sm font-medium">Domination Progress</span>
                                            <span className="text-sm text-gray-600">{dashboardData?.competitive_position?.domination_progress || 0}%</span>
                                        </div>
                                        <Progress value={dashboardData?.competitive_position?.domination_progress || 0} />
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    </TabsContent>

                    {/* Threat Management Tab */}
                    <TabsContent value="threats" className="space-y-6">
                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center">
                                    <AlertTriangle className="h-5 w-5 mr-2" />
                                    Active Threats
                                </CardTitle>
                                <CardDescription>Current competitive threats requiring automated response</CardDescription>
                            </CardHeader>
                            <CardContent>
                                <div className="space-y-4">
                                    {activeThreats.map((threat) => (
                                        <div key={threat.id} className="flex items-center justify-between p-4 border rounded-lg">
                                            <div className="flex items-center space-x-4">
                                                {getStatusIcon(threat.automated_response_status)}
                                                <div>
                                                    <div className="font-medium">{threat.source_competitor} - {threat.threat_type}</div>
                                                    <div className="text-sm text-gray-600">
                                                        Detected: {new Date(threat.detection_timestamp).toLocaleString()}
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="flex items-center space-x-2">
                                                <Badge variant={getSeverityColor(threat.threat_severity)}>
                                                    {threat.threat_severity}
                                                </Badge>
                                                {threat.automated_response_status === 'pending_approval' && (
                                                    <Button
                                                        size="sm"
                                                        onClick={() => deployAutomatedResponse(threat.id)}
                                                    >
                                                        <Play className="h-4 w-4 mr-1" />
                                                        Deploy Response
                                                    </Button>
                                                )}
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </CardContent>
                        </Card>

                        <Card>
                            <CardHeader>
                                <CardTitle>Threat Detection Rules</CardTitle>
                                <CardDescription>Automated threat detection configuration</CardDescription>
                            </CardHeader>
                            <CardContent>
                                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                    <div className="text-center">
                                        <div className="text-2xl font-bold text-blue-600">{threatData?.total_rules || 0}</div>
                                        <div className="text-sm text-gray-600">Total Rules</div>
                                    </div>
                                    <div className="text-center">
                                        <div className="text-2xl font-bold text-green-600">{threatData?.active_rules || 0}</div>
                                        <div className="text-sm text-gray-600">Active Rules</div>
                                    </div>
                                    <div className="text-center">
                                        <div className="text-2xl font-bold text-purple-600">{threatData?.threats_detected_today || 0}</div>
                                        <div className="text-sm text-gray-600">Detected Today</div>
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    </TabsContent>

                    {/* Opportunities Tab */}
                    <TabsContent value="opportunities" className="space-y-6">
                        <Card>
                            <CardHeader>
                                <CardTitle className="flex items-center">
                                    <Target className="h-5 w-5 mr-2" />
                                    Market Opportunities
                                </CardTitle>
                                <CardDescription>Identified opportunities for competitive advantage</CardDescription>
                            </CardHeader>
                            <CardContent>
                                <div className="space-y-4">
                                    {opportunities.map((opportunity) => (
                                        <div key={opportunity.id} className="flex items-center justify-between p-4 border rounded-lg">
                                            <div className="flex items-center space-x-4">
                                                <div>
                                                    <div className="font-medium">{opportunity.opportunity_source}</div>
                                                    <div className="text-sm text-gray-600">
                                                        Value: {formatCurrency(opportunity.estimated_value)} â€¢
                                                        Success: {formatPercentage(opportunity.capture_probability)}
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="flex items-center space-x-2">
                                                <Badge variant="outline">{opportunity.deployment_status.replace(/_/g, ' ')}</Badge>
                                                <Button
                                                    size="sm"
                                                    onClick={() => captureMarketOpportunity(opportunity.id)}
                                                >
                                                    <Rocket className="h-4 w-4 mr-1" />
                                                    Capture
                                                </Button>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </CardContent>
                        </Card>
                    </TabsContent>

                    {/* Performance Tab */}
                    <TabsContent value="performance" className="space-y-6">
                        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                            <Card>
                                <CardHeader>
                                    <CardTitle className="flex items-center">
                                        <BarChart3 className="h-5 w-5 mr-2" />
                                        Performance Metrics
                                    </CardTitle>
                                </CardHeader>
                                <CardContent className="space-y-4">
                                    <div className="space-y-3">
                                        <div className="flex justify-between">
                                            <span className="text-sm">Response Time Improvement</span>
                                            <span className="font-medium">{performanceMetrics?.response_time_improvement || 0}%</span>
                                        </div>
                                        <div className="flex justify-between">
                                            <span className="text-sm">Automation Effectiveness</span>
                                            <span className="font-medium">{performanceMetrics?.automation_effectiveness || 0}%</span>
                                        </div>
                                        <div className="flex justify-between">
                                            <span className="text-sm">Competitive Advantage</span>
                                            <span className="font-medium">{performanceMetrics?.competitive_advantage_maintained || 0}%</span>
                                        </div>
                                        <div className="flex justify-between">
                                            <span className="text-sm">Market Share Protection</span>
                                            <span className="font-medium">{performanceMetrics?.market_share_protection || 0}%</span>
                                        </div>
                                    </div>
                                </CardContent>
                            </Card>

                            <Card>
                                <CardHeader>
                                    <CardTitle className="flex items-center">
                                        <Users className="h-5 w-5 mr-2" />
                                        Activity Summary
                                    </CardTitle>
                                </CardHeader>
                                <CardContent className="space-y-4">
                                    <div className="grid grid-cols-2 gap-4">
                                        <div className="text-center">
                                            <div className="text-2xl font-bold text-blue-600">{performanceMetrics?.threats_managed || 0}</div>
                                            <div className="text-sm text-gray-600">Threats Managed</div>
                                        </div>
                                        <div className="text-center">
                                            <div className="text-2xl font-bold text-green-600">{performanceMetrics?.opportunities_captured || 0}</div>
                                            <div className="text-sm text-gray-600">Opportunities Captured</div>
                                        </div>
                                        <div className="text-center">
                                            <div className="text-2xl font-bold text-purple-600">{performanceMetrics?.automated_responses || 0}</div>
                                            <div className="text-sm text-gray-600">Automated Responses</div>
                                        </div>
                                        <div className="text-center">
                                            <div className="text-2xl font-bold text-yellow-600">{performanceMetrics?.roi_on_automated_responses || 0}%</div>
                                            <div className="text-sm text-gray-600">ROI</div>
                                        </div>
                                    </div>
                                </CardContent>
                            </Card>
                        </div>
                    </TabsContent>

                    {/* Controls Tab */}
                    <TabsContent value="controls" className="space-y-6">
                        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                            <Card>
                                <CardHeader>
                                    <CardTitle className="flex items-center">
                                        <Settings className="h-5 w-5 mr-2" />
                                        Quick Actions
                                    </CardTitle>
                                    <CardDescription>Execute immediate strategic responses</CardDescription>
                                </CardHeader>
                                <CardContent className="space-y-4">
                                    <Button
                                        className="w-full"
                                        onClick={() => triggerPricingAdjustment('competitive_response', ['fresha', 'booksy'], 'moderate')}
                                    >
                                        <DollarSign className="h-4 w-4 mr-2" />
                                        Trigger Pricing Adjustment
                                    </Button>
                                    <Button
                                        variant="outline"
                                        className="w-full"
                                        onClick={() => generateResponseReport()}
                                    >
                                        <Download className="h-4 w-4 mr-2" />
                                        Generate Strategic Report
                                    </Button>
                                    <Button
                                        variant="outline"
                                        className="w-full"
                                        onClick={() => window.location.reload()}
                                    >
                                        <RotateCcw className="h-4 w-4 mr-2" />
                                        Refresh Dashboard
                                    </Button>
                                </CardContent>
                            </Card>

                            <Card>
                                <CardHeader>
                                    <CardTitle className="flex items-center">
                                        <Eye className="h-5 w-5 mr-2" />
                                        System Status
                                    </CardTitle>
                                    <CardDescription>Real-time system health and capabilities</CardDescription>
                                </CardHeader>
                                <CardContent className="space-y-4">
                                    {dashboardData?.response_capabilities && Object.entries(dashboardData.response_capabilities).map(([key, value]) => (
                                        <div key={key} className="flex items-center justify-between">
                                            <span className="text-sm font-medium capitalize">{key.replace(/_/g, ' ')}</span>
                                            <Badge variant={String(value) === 'active' || String(value) === 'real_time' ? 'default' : 'secondary'}>
                                                {String(value)}
                                            </Badge>
                                        </div>
                                    ))}
                                </CardContent>
                            </Card>
                        </div>

                        <Card>
                            <CardHeader>
                                <CardTitle>Upcoming Strategic Actions</CardTitle>
                                <CardDescription>Pre-configured automated responses ready for deployment</CardDescription>
                            </CardHeader>
                            <CardContent>
                                <div className="space-y-3">
                                    {dashboardData?.upcoming_actions?.map((action: string, index: number) => (
                                        <div key={index} className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                                            <div className="flex items-center">
                                                <Rocket className="h-4 w-4 text-blue-600 mr-2" />
                                                <span className="text-sm font-medium">{action}</span>
                                            </div>
                                            <Button size="sm" variant="outline">
                                                Deploy
                                            </Button>
                                        </div>
                                    ))}
                                </div>
                            </CardContent>
                        </Card>
                    </TabsContent>
                </Tabs>
            </div>
        </div>
    );
}

import React, { useState } from 'react';
import { Menu, X } from 'lucide-react';

interface NavigationProps {
    onNavigate: (section: string) => void;
    isMenuOpen: boolean;
    setIsMenuOpen: (open: boolean) => void;
}

export const Navigation: React.FC<NavigationProps> = ({
    onNavigate,
    isMenuOpen,
    setIsMenuOpen,
}) => {
    const navItems = ['Home', 'Services', 'Products', 'Gallery', 'Testimonials'];

    return (
        <header className="sticky top-0 z-50 bg-white/95 backdrop-blur-sm shadow-sm transition-all duration-300">
            <div className="container mx-auto px-4 py-3">
                <div className="flex justify-between items-center">
                    {/* Logo */}
                    <div className="flex items-center gap-3 cursor-pointer" onClick={() => onNavigate('home')}>
                        <div className="relative w-14 h-14 md:w-20 md:h-20">
                            <img
                                src="/logos/instyle-logo.png"
                                alt="InStyle Logo"
                                className="object-contain w-full h-full"
                            />
                        </div>
                        <span className="text-2xl md:text-3xl font-bold font-serif leading-none text-crimson-primary">
                            InStyle <br className="hidden md:block" /> <span className="text-sm md:text-lg font-sans font-normal text-gray-600 block md:inline">Hair Boutique</span>
                        </span>
                    </div>

                    {/* Desktop Nav */}
                    <nav className="hidden md:flex items-center gap-8">
                        {navItems.map((item) => (
                            <button
                                key={item}
                                onClick={() => onNavigate(item.toLowerCase())}
                                className="text-sm font-medium hover:text-crimson-primary transition-colors uppercase tracking-wide"
                            >
                                {item}
                            </button>
                        ))}
                        <button
                            onClick={() => onNavigate('booking')}
                            className="px-6 py-2.5 rounded-full text-white font-medium transition-transform hover:-translate-y-0.5 shadow-lg hover:shadow-xl bg-crimson-primary hover:bg-crimson-dark"
                        >
                            Book Now
                        </button>
                    </nav>

                    {/* Mobile Menu Button */}
                    <button
                        className="md:hidden p-2 text-gray-600"
                        onClick={() => setIsMenuOpen(!isMenuOpen)}
                    >
                        {isMenuOpen ? <X /> : <Menu />}
                    </button>
                </div>
            </div>

            {/* Mobile Nav */}
            {isMenuOpen && (
                <div className="md:hidden absolute top-full left-0 w-full bg-white border-t shadow-lg py-4 px-4 flex flex-col gap-4">
                    {navItems.map((item) => (
                        <button
                            key={item}
                            onClick={() => onNavigate(item.toLowerCase())}
                            className="text-left py-2 font-medium border-b border-gray-100"
                        >
                            {item}
                        </button>
                    ))}
                    <button
                        onClick={() => onNavigate('booking')}
                        className="w-full py-3 rounded-lg text-white font-bold text-center mt-2 bg-crimson-primary hover:bg-crimson-dark"
                    >
                        Book Appointment
                    </button>
                </div>
            )}
        </header>
    );
};

import React from 'react';
import Link from 'next/link';
import { ArrowRight, Heart, ShoppingCart } from 'lucide-react';

interface Product {
    id: string;
    name: string;
    description: string;
    price: number; // in cents
    image_url?: string;
}

interface ProductsSectionProps {
    products: Product[];
}

/**
 * Products section component displaying available hair products
 * Features product grid with wishlist and cart functionality
 */
export const ProductsSection: React.FC<ProductsSectionProps> = ({ products }) => {
    /**
     * Format product price from cents to Rands
     * @param priceInCents - Price in cents
     * @returns Formatted price string
     */
    const formatPrice = (priceInCents: number): string => {
        return `R${(priceInCents / 100).toFixed(0)}`;
    };

    /**
     * Get default product image if none provided
     * @param productName - Product name for context
     * @returns Default image URL
     */
    const getDefaultImage = (productName: string): string => {
        return "https://images.unsplash.com/photo-1526947425960-945c6e72858f?auto=format&fit=crop&w=500&q=80";
    };

    /**
     * Handle add to cart action
     * @param productId - Product ID to add to cart
     */
    const handleAddToCart = (productId: string): void => {
        // TODO: Implement cart functionality
        console.log('Add to cart:', productId);
    };

    /**
     * Handle wishlist toggle
     * @param productId - Product ID to toggle in wishlist
     */
    const handleWishlistToggle = (productId: string): void => {
        // TODO: Implement wishlist functionality
        console.log('Toggle wishlist:', productId);
    };

    return (
        <section id="products" className="py-20 bg-[#F9F9F9]" role="region" aria-labelledby="products-heading">
            <div className="container mx-auto px-4">
                {/* Section header */}
                <div className="flex flex-col md:flex-row justify-between items-end mb-12 gap-6">
                    <div>
                        <h2 id="products-heading" className="text-3xl md:text-4xl font-bold font-serif mb-4 text-gray-900">
                            Premium Hair Products
                        </h2>
                        <p className="text-gray-600">
                            Shop our curated selection of professional hair care.
                        </p>
                    </div>

                    {/* View all products link */}
                    <Link
                        href="/book/instylehairboutique/shop"
                        className="text-crimson-primary font-semibold flex items-center gap-2 hover:gap-3 transition-all focus:outline-none focus:ring-2 focus:ring-crimson-primary focus:ring-opacity-50 rounded-lg px-2 py-1"
                        aria-label="View all hair products"
                    >
                        View All Products
                        <ArrowRight className="w-5 h-5" aria-hidden="true" />
                    </Link>
                </div>

                {/* Products grid */}
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                    {products.map((product, index) => (
                        <article
                            key={product.id || index}
                            className="bg-white rounded-xl p-4 shadow-md hover:shadow-xl transition-all group"
                        >
                            {/* Product image */}
                            <div className="relative h-48 bg-gray-100 rounded-lg mb-4 overflow-hidden flex items-center justify-center">
                                <img
                                    src={product.image_url || getDefaultImage(product.name)}
                                    alt={product.name}
                                    width={200}
                                    height={192}
                                    className="object-cover transition-transform duration-500 group-hover:scale-110"
                                    loading="lazy"
                                />

                                {/* Wishlist button */}
                                <button
                                    onClick={() => handleWishlistToggle(product.id)}
                                    className="absolute bottom-3 right-3 w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center text-gray-600 hover:text-red-500 hover:bg-red-50 transition-all translate-y-10 group-hover:translate-y-0 z-10 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50"
                                    aria-label={`Add ${product.name} to wishlist`}
                                    aria-pressed="false"
                                >
                                    <Heart className="w-5 h-5" aria-hidden="true" />
                                </button>
                            </div>

                            {/* Product details */}
                            <div className="space-y-2">
                                <h3 className="font-semibold text-gray-900 text-sm leading-tight">
                                    {product.name}
                                </h3>

                                {/* Product description */}
                                {product.description && (
                                    <p className="text-xs text-gray-500 line-clamp-2">
                                        {product.description}
                                    </p>
                                )}

                                {/* Price and add to cart */}
                                <div className="flex items-center justify-between pt-2">
                                    <span className="font-bold text-crimson-primary text-lg">
                                        {formatPrice(product.price)}
                                    </span>

                                    <button
                                        onClick={() => handleAddToCart(product.id)}
                                        className="p-2 rounded-full bg-gray-50 hover:bg-[#C0392B] hover:text-white transition-colors focus:outline-none focus:ring-2 focus:ring-[#C0392B] focus:ring-opacity-50"
                                        aria-label={`Add ${product.name} to cart`}
                                    >
                                        <ShoppingCart className="w-4 h-4" aria-hidden="true" />
                                    </button>
                                </div>
                            </div>
                        </article>
                    ))}
                </div>

                {/* Empty state */}
                {products.length === 0 && (
                    <div className="text-center py-12">
                        <div className="w-24 h-24 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
                            <ShoppingCart className="w-8 h-8 text-gray-400" aria-hidden="true" />
                        </div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-2">No products available</h3>
                        <p className="text-gray-600">
                            Check back soon for our premium hair care products.
                        </p>
                    </div>
                )}
            </div>
        </section>
    );
};

export default ProductsSection;

import React from 'react';
import { Clock } from 'lucide-react';

interface Service {
    id: string;
    name: string;
    description: string;
    price: number; // in cents
    duration_minutes: number;
}

interface ServicesSectionProps {
    services: Service[];
    onNavigate: (section: string) => void;
}

/**
 * Services section component displaying available hair services
 * Features service cards with pricing and booking CTAs
 */
export const ServicesSection: React.FC<ServicesSectionProps> = ({
    services,
    onNavigate,
}) => {
    /**
     * Get service image based on service name
     * @param name - Service name to match against patterns
     * @returns Image URL for the service
     */
    const getServiceImage = (name: string): string => {
        const lowerName = name.toLowerCase();
        if (lowerName.includes('install')) {
            return "https://images.unsplash.com/photo-1562322140-8baeececf3df?auto=format&fit=crop&w=800&q=80";
        }
        if (lowerName.includes('maphondo') || lowerName.includes('braid')) {
            return "https://images.unsplash.com/photo-1605497788044-5a32c7078486?auto=format&fit=crop&w=800&q=80";
        }
        if (lowerName.includes('makeup') || lowerName.includes('glam')) {
            return "https://images.unsplash.com/photo-1487412947132-232984567455?auto=format&fit=crop&w=800&q=80";
        }
        if (lowerName.includes('ponytail')) {
            return "https://images.unsplash.com/photo-1580618672591-eb180b1a973f?auto=format&fit=crop&w=800&q=80";
        }
        return "https://images.unsplash.com/photo-1560869713-7d0a29430803?auto=format&fit=crop&w=800&q=80";
    };

    /**
     * Get service badge based on service name
     * @param name - Service name to match against patterns
     * @returns Badge text or null
     */
    const getServiceBadge = (name: string): string | null => {
        const lowerName = name.toLowerCase();
        if (lowerName.includes('middle') || lowerName.includes('side')) {
            return "Popular";
        }
        if (lowerName.includes('maphondo')) {
            return "Signature";
        }
        if (lowerName.includes('frontal')) {
            return "Premium";
        }
        return null;
    };

    /**
     * Format service price from cents to Rands
     * @param priceInCents - Price in cents
     * @returns Formatted price string
     */
    const formatPrice = (priceInCents: number): string => {
        return `R${(priceInCents / 100).toFixed(0)}`;
    };

    return (
        <section id="services" className="py-20 bg-white" role="region" aria-labelledby="services-heading">
            <div className="container mx-auto px-4">
                {/* Section header */}
                <div className="text-center mb-16">
                    <h2 id="services-heading" className="text-3xl md:text-4xl font-bold font-serif mb-4 text-gray-900">
                        Our Premium Services
                    </h2>
                    <p className="text-gray-600 max-w-2xl mx-auto">
                        Professional hair treatments and styling services tailored to your unique needs.
                    </p>
                </div>

                {/* Services grid */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                    {services.map((service, index) => {
                        const badge = getServiceBadge(service.name);
                        const imageUrl = getServiceImage(service.name);

                        return (
                            <article
                                key={service.id || index}
                                className="group bg-white rounded-2xl overflow-hidden shadow-lg hover:shadow-2xl transition-all duration-300 border border-gray-100"
                            >
                                {/* Service image */}
                                <div className="relative h-64 overflow-hidden">
                                    {badge && (
                                        <div className="absolute top-4 right-4 z-10 bg-[#1B1B1B] text-white text-xs font-bold px-3 py-1 rounded-full uppercase tracking-wider">
                                            {badge}
                                        </div>
                                    )}
                                    <img
                                        src={imageUrl}
                                        alt={`${service.name} service illustration`}
                                        width={500}
                                        height={400}
                                        className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                        loading="lazy"
                                    />
                                </div>

                                {/* Service details */}
                                <div className="p-6">
                                    <h3 className="text-xl font-bold mb-2 text-gray-900">{service.name}</h3>
                                    <p className="text-gray-600 mb-4 text-sm line-clamp-2">{service.description}</p>

                                    {/* Price and duration */}
                                    <div className="flex items-center justify-between mb-6">
                                        <span className="text-2xl font-bold text-crimson-primary">
                                            {formatPrice(service.price)}
                                        </span>
                                        <div className="flex items-center gap-1 text-amber-500 text-sm">
                                            <Clock className="w-4 h-4" aria-hidden="true" />
                                            <span className="font-medium">{service.duration_minutes}m</span>
                                        </div>
                                    </div>

                                    {/* Booking CTA */}
                                    <button
                                        onClick={() => onNavigate('booking')}
                                        className="w-full py-3 rounded-xl font-semibold border-2 border-crimson-primary text-crimson-primary hover:bg-crimson-primary hover:text-white transition-all flex items-center justify-center gap-2 focus:outline-none focus:ring-2 focus:ring-crimson-primary focus:ring-opacity-50"
                                        aria-label={`Book ${service.name} appointment`}
                                    >
                                        Book Now
                                    </button>
                                </div>
                            </article>
                        );
                    })}
                </div>
            </div>
        </section>
    );
};

export default ServicesSection;
